import React, {useState,useRef, useEffect} from 'react';
import * as d3 from "d3";
import './LineChart.css'

const LineChart=()=>{
    const d3Chart=useRef();
    useEffect(()=>{
        fetch('https://data.cityofnewyork.us/resource/tg4x-b46p.json')
        .then(response=>response.json())
        .then(data=>
            {const permit=data.filter(type=>
                {
                    return type.eventtype==="Shooting Permit" 
                }

            )
            // console.log(permit)
            const dates= [...new Set(permit.map((item)=>item.enteredon.slice(0,10)))]
            let countArray=[]
            dates.map((item)=>
            {
                let date=item;
                let count=0
                permit.map((val)=>{
                    if(val.enteredon.slice(0,10)===date){
                        count=count+1;
                    }

                })
                countArray.push({date:date,count:count})
            })
            const margin={top:20,right:30,bottom:30,left:30}
            const width=parseInt(d3.select('#d3demo').style('width'))- margin.left - margin.right
            const height=parseInt(d3.select('#d3demo').style('height'))-margin.top - margin.bottom

            const svg=d3.select(d3Chart.current)
                        .attr('width',width + margin.left + margin.right)
                        .attr('height',height + height + margin.top + margin.bottom)
                        .style('background-color','yellow')
                        .append('g')
                        .attr('transform', 'translate('+ margin.left + ',' + margin.top + ')');

            // const x=d3.scaleTime()
            //         .domain(d3.extent(countArray,function(d){return d.date}))
            //         .range([0,width])
            //         console.log(x(function(d){return d.date}))
            // svg.append('g')
            // .attr('transform','translate(0,'+height+')')
            // .call(d3.axisBottom(x))

            const max=d3.max(countArray,function(d){return d.count})+5
            debugger;

            const y=d3.scaleLinear()
                    .domain([0,max])
                    .range([height,0])
            svg.append('g')
            .call(d3.axisLeft(y))

            // svg.append('path')
            // .datum(countArray)
            // .attr('fill','none')
            // .attr('stroke','white')
            // .attr('stroke-width','3')
            // .attr('d',d3.line()
            //             .x(function(d){return x(d.date)}))
            //             .y(function(d){return y(d.count)})

            }
        )})
    return(
        <div id="d3demo">
            <svg ref={d3Chart}>

            </svg>
        </div>
    )

}

export default LineChart;
